body.script:
// prosty efekt animacji — awatar unosi się i obraca
let angle = 0;
let up = true;

setInterval(function() {
    // ruch góra/dół
    let y = up ? -10 : 10;
    $("#avatar").css("transform", "translateY(" + y + "px) rotate(" + angle + "deg)");

    // zmiana kierunku
    up = !up;
    angle += 10;
}, 500);
