body.script:
// Gdy strona się załaduje, uruchom animację
$(function() {
    // Wybierz obrazek wewnątrz .avatar (używając selektora potomka)
    $(".avatar img").animate(
        {
            width: "250px"   // powiększ awatar
        },
        1000                 // czas trwania animacji: 1 sekunda
    );
});
